{
  "练气期": {
    "lifespan": "相较于凡人百年光阴，此境界修士的寿元并无显著增长，但因灵气滋养，通常能无病无灾地活到天年。",
    "core_concept": "引气入体，开辟气海。",
    "description": "修行的第一步，引天地灵气入体，在丹田内开辟“气海”。此阶段共分九个小境界（一层至九层）。修士的本质仍是凡人，只是拥有了微弱的灵力，能施展一些基础的法术，身体素质略强于常人。",
    "breakthrough_trial": "无",
    "failure_consequence": "道基会因灵力冲击产生损伤，跌落一个小境界，需要耗费时日进行温养。"
  },
  "筑基期": {
    "lifespan": "突破此境后，修士的寿元将迎来第一次质的飞跃，是凡人寿命的两倍。",
    "core_concept": "开辟紫府，凝气成液。",
    "description": "修士真正脱离凡俗的开始。在体内开辟“丹田紫府”，筑下万丈高楼的道基。此境界分为九层，修士体内的灵力会由气态逐渐凝为液态，为日后结丹做准备。",
    "breakthrough_trial": "【三九雷劫】。考验本质为【肉身】，旨在考验修士的凡胎能否承受天地灵力的洗礼。景象为二十七道天雷接连轰落。",
    "failure_consequence": "道基会因雷劫出现裂痕，必须进行为期三年的闭关疗伤。"
  },
  "金丹期": {
    "lifespan": "寿元大幅增长。",
    "core_concept": "碎液成丹，自成源头。",
    "description": "将体内所有液态灵力压缩凝炼，在丹田内形成一颗固态的“金丹”，修士从此由灵力管道转变为灵力源头。此境界分为九个小层次，高阶金丹表面会浮现“丹纹”。神识可离体。",
    "breakthrough_trial": "【六九心魔雷劫】。考验本质为【心魔】，是内外双重考验，旨在审视修士的道心是否纯粹。景象为五十四道天雷，且雷声中会夹杂着诱人堕落的魔音，直击神魂，引动内心最深的恐惧与欲望。",
    "failure_consequence": "金丹当场碎裂，修为尽废，道途断绝。幸存者道心也会破碎，难以恢复。"
  },
  "元婴期": {
    "lifespan": "突破此境，修士寿元是凡人寿命的五倍。",
    "core_concept": "碎丹成婴，神魂化形。",
    "description": "破碎金丹，将其本源能量与神魂融合，在紫府内孕育出一个与自身面貌相同的“元婴”。元婴是修士的“第二条性命”，核心能力为【元婴出窍】。若肉身被毁，元婴有机会逃离并【夺舍】重生。",
    "breakthrough_trial": "【元神寂灭雷劫】。考验本质为【元神】，是专门针对“元婴”的精准打击。天雷之力将穿透肉身与法宝的层层守护，直击修士的根本。景象为天雷化为无形无质的寂灭之光，看似威力不显，实则凶险万分。",
    "failure_consequence": "元婴溃散，境界跌落回金丹期大圆满，且神魂遭受不可逆转的重创，终生无法再次凝婴。"
  },
  "化神期": {
    "lifespan": "突破此境，修士寿元是凡人寿命的十倍。",
    "core_concept": "神融天地，言出法随。",
    "description": "将自身元神融入天地法则，与之产生共鸣。修行转为对自身“大道”的感悟与积累。可【神游太虚】，意念感知千里，并能初步干涉现实世界的元素。",
    "breakthrough_trial": "【法则之雷】。考验本质为【道】，是天道对修士所选道路的直接审判。景象不再是单纯的雷电。剑修将面对无尽剑之法则的锋锐斩杀；丹修则要承受生命法则的枯荣轮转。",
    "failure_consequence": "身死道消。肉身与元神在法则之力下被彻底分解抹去。"
  },
  "合体期": {
    "lifespan": "突破此境，修士寿元将增长至两千至三千年。",
    "core_concept": "身魂合一，与道合真。",
    "description": "肉身与元神达到完美的融合，不再有内外之分，并开始与一方天地法则相合。修士自身即是一个“小天地”，举手投足间便能引动天地之力。",
    "breakthrough_trial": "【身魂合一雷劫】",
    "failure_consequence": "身死道消。身魂被强行分离，引发体内能量失控爆炸，彻底从世间抹去。"
  },
  "大乘期": {
    "lifespan": "突破此境，修士寿元可达五千年以上。",
    "core_concept": "道满自溢，人间极致。",
    "description": "此界修行的终点。修士一身之道法已然圆满，无需刻意吐纳修行，其一呼一吸皆在与大道共鸣，修为会自行增长，是为“道满自溢”。是行走于人间的法则化身。",
    "breakthrough_trial": "【九九归真天劫】。考验本质为【因果】，是对修士一生所修之道、所结因果的全面审判。景象共八十一重，包含了心魔、业火、时间乱流、空间风暴等多种形态。",
    "failure_consequence": "身死道消或迷失虚空。"
  }
}

