{
  "宁羽白": { "status": "独立核心档案" }, "苏酒儿": { "status": "独立核心档案" },
  "萧咏凝": { "status": "独立核心档案" }, "青螭": { "status": "独立核心档案" },
  "公孙滢": { "status": "独立核心档案" }, "敖幼宁": { "status": "独立核心档案" },
  "幻蝶": { "status": "独立核心档案" }, "林冰语": { "status": "独立核心档案" },
  "耿仙仙": { "status": "独立核心档案" },
  "紫修": {
    "title": "烈日剑、碧落剑派代掌门", "faction": "碧落剑派", "realm": "化神后期", "alignment": "掌权派",
    "personality": "心狠手辣与野心勃勃。为达目的可以牺牲任何人，践踏任何道义。极度自大且虚伪，内心无法容忍任何在天赋或声望上超越自己的人存在。",
    "background": "“碧落之乱”的真正主谋。因嫉妒师弟【紫微】的天赋而被立为代掌门，遂与【云界仙宫】的【岳临风】暗中结盟。他不仅利用【摄魂符】陷害挚友，更是在乱局之前便已潜入密室，亲手谋害了对他信任有加的恩师【碧落真人】。",
    "appearance": "身高六尺，常年一袭青布道袍，显得精神矍铄。双目开阖间有神光电射，周身散发着不怒自威的强大气势，一派宗师风范。然而，在这副威严公正的表象之下，其眼神深处却藏着一丝不易察-察觉的阴狠。",
    "techniques": ["《碧落九霄诀 - 真阳诀》：剑出如烈日当空，霸道绝伦，其灼热的剑气足以熔金化铁，是碧落剑派攻击力最强的剑诀之一。"]
  },
  "紫霆": {
    "title": "雷鸣剑、戒律长老", "faction": "碧落剑派", "realm": "化神中期", "alignment": "掌权派",
    "personality": "城府极深，为人阴险狡诈，是代掌门【紫修】最忠诚的追随者与最得力的左膀右臂。",
    "background": "在宗门之内，他负责执掌戒律，手段酷烈无情，是所有普通弟子心中最为畏惧的存在。“碧落之乱”阴谋中除紫修外最核心的帮凶，是多个关键环节最坚定的执行者。",
    "appearance": "身形干枯瘦小，头戴一顶桑木道冠，脸上布满了深刻的皱纹，一双手干瘦得如同鸡爪。气质阴鸷，不苟言笑，冰冷的眼神中不带丝毫情感，令人望之不寒而栗。",
    "techniques": ["《碧落九霄诀 - 轰雷诀》：剑出则雷声轰鸣，电光闪烁，充满了审判与毁灭的气息，是碧落剑派中最为刚猛霸道的剑诀之一。"]
  },
  "紫虚": {
    "title": "暴雨剑、传功长老", "faction": "碧落剑派", "alignment": "护道派 (潜在)",
    "personality": "内心本质上是一位保有善念的正直之士，但天性懦弱，极度畏惧强权。谁也不得罪的“老好人”，对【紫修】一派的指令时常阳奉阴违，却又绝不敢公然反抗。",
    "background": "灵魂深陷于对十年前“碧落之乱”中选择沉默的无尽愧疚，与对掌权派报复的深深恐惧之间。这份心魔让他修为停滞不前。他知晓当年【紫修】使用【摄魂符】的部分关键真相，是可以被争取的关键人物。",
    "appearance": "常年一身葛衣道人打扮，身形微胖，神情中总是带着一丝挥之不去的忧愁与为难。手持拂尘，背后负着一柄似乎从未出鞘的长剑。",
    "techniques": ["《碧落九霄诀 - 碎雨诀》：因心魔所困，其剑法修为已多年未有寸进。"]
  },
  "紫寒": {
    "title": "霜月剑、内务长老", "faction": "碧落剑派", "realm": "化神初期", "alignment": "掌权派",
    "personality": "气质孤僻冰冷，仿佛一块万年不化的玄冰，意志如钢铁般坚硬，极难被任何外物所动摇。",
    "background": "“碧落之乱”的同谋者之一。她代表了宗门中一部分极端保守的势力，厌恶【紫微】不拘世俗的行事风格，认为铲除他是必要的“拨乱反正”。",
    "appearance": "作道姑打扮的清冷妇人，常年身着一袭素白衣裙。",
    "techniques": ["《碧落九霄诀 - 月华诀》：剑法如其人，清冷而致命。剑光挥洒间，如同清冷的月华洒落，美丽之中暗藏杀机。"]
  },
  "沈从龙": {
    "title": "飞星剑、外事长老", "faction": "碧落剑派", "realm": "化神初期", "alignment": "掌权派",
    "personality": "八面玲珑，极善钻营，脸上总是挂着和气的笑容。是趋炎附势、唯利是图的投机者，人生信条是“利益至上”。",
    "background": "“碧落之乱”中最积极的投机者与受益人，目的是为自己谋取“外事长老”这一肥缺。",
    "techniques": ["《碧落九霄诀 - 挪星诀》：不重正面强攻，而擅长身法挪移与时机把握。剑招如同夜空中划过的流星，轨迹莫测。"]
  },
  "陆玉宁": {
    "title": "晴雪剑、雪闲馆馆主", "faction": "碧落剑派", "status": "被囚禁于后山玉灵洞", "alignment": "护道派",
    "personality": "天性善良，心软且重情义。碧落剑派的重要人物，碧落之乱的见证者和受害者，代表了碧落派中正直坚守的一脉。",
    "background": "碧落真人之养女，曾是宗门最受宠爱的小师妹。“碧落之乱”中唯一幸存且知晓全部真相的关键证人，是重振碧落剑派的唯一希望。",
    "appearance": "外貌雍容秀美，气质温和，一双眼眸柔和得如同映着冬雪的湖面，宁静而纯澈。",
    "techniques": ["《碧落九霄诀 - 雪舞诀》", "【雪燕双飞】：赖以成名的双剑武技，剑出如两只在暴雪中穿行的飞燕，轨迹迅捷而又飘忽不定。"],
    "core_ability": "【凛化成冰】：剑意与心境相连，能在周身形成一片无形的寒气领域，削弱敌人战意，迟缓其行动。"
  },
  "紫微真人": {
    "title": "浮云剑、碧落七子之一", "faction": "碧落剑派", "status": "已故",
    "personality": "性情温润如玉，气质恬淡出尘，仿佛一片逍遥于九天之上的浮云，对世俗的权位与纷争毫无兴趣。不拘泥于正邪之别与种族之见。",
    "background": "原碧落剑派数百年不遇的绝世奇才，【宁羽白】的恩师，与魔道的【夏侯屠】、妖族的【青螭】父母皆为至交。在“碧落之乱”中被诬陷，最终为保护弟子亡于圣使【血隐】之手。他将完整的《碧落七心决》传给了宁羽白。",
    "techniques": ["《碧落七心决》"]
  },
  "沈开玉": {
    "title": "碧落剑派弟子", "faction": "碧落剑派", "realm": "筑基期九层", "alignment": "掌权派",
    "personality": "心高气傲，贪玩好色，虚荣狡猾，小心眼。是门派衰败背景下，一些人选择背弃同门依附强者的缩影。",
    "background": "外事长老【沈从龙】之子，根基不稳。与有婚约的凌霄派【赵文婷】私通，同时又追求秋水剑派的【梁雅儿】。",
    "appearance": "年约十七八岁，面如白玉，唇若涂朱，头戴金冠，身着锦袍，一表人才。",
    "techniques": ["《碧落九霄诀 - 挪星诀》"]
  },
  "曹梦秀": {
    "title": "陆玉宁弟子", "faction": "碧落剑派", "realm": "筑基期", "alignment": "护道派",
    "personality": "正直，重情义。",
    "background": "“碧落之乱”后依然坚守在师父【陆玉宁】身边，是碧落剑派年轻一代正直重情义的成员，与师父一同肩负重振碧落的责任。",
    "techniques": ["《碧落九霄诀 - 碎雨诀》"]
  },
  "刘慕平": {
    "title": "碧落剑派杂役院主事", "faction": "碧落剑派", "alignment": "掌权派",
    "personality": "阴沉、刻板、气量狭小。",
    "background": "【紫修】的三弟子，是碧落剑派的基层权力象征，长期负责筛选培养初级弟子。",
    "appearance": "葛袍赤须老者，身材中等。"
  },
  "谢青阳": {
    "title": "紫虚弟子", "faction": "碧落剑派", "realm": "筑基期", "alignment": "护道派 (潜在)",
    "personality": "博闻强识，擅长信息分析和策略制定。",
    "background": "与<User>同期加入碧落剑派的杂役，后成功筑基拜入【紫虚】门下。是主角获取信息、分析局势的重要帮手，与门派内部正直力量保持着联系。"
  },
  "巫主": {
    "title": "圣使组织领袖", "faction": "圣使组织",
    "personality": "智慧如海，心性如渊。一个背负着整个种族命运的“历史修正主义者”。以绝对的理智追求着逆转时空的终极目标，视包括七色圣使在内的所有生灵为达成此目标的棋子。",
    "background": "上古巫族遗孤，“圣使组织”的创立者与唯一主宰。继承了远古大巫的部分知识，包括对“九九归墟”的理解。旨在集齐【天道八宝】，改写历史，让巫族成为永恒主宰。",
    "abilities": ["实力远超常规修行体系，可能已触及时间和因果的法则。", "掌握诡异的巫族秘法，能以某种形式“复活”麾下的七色圣使。"]
  },
  "血隐": {
    "title": "赤圣使", "faction": "圣使组织",
    "personality": "首席刺客，身份最为神秘，行踪不定。",
    "background": "亲手格杀【紫微真人】的元凶。五百年前以“天邪道”之名发动战争，击杀了【秋水剑派】前代高手与【夏侯世家】满门，后被剑神【卫淳风】与剑宗【穆少游】联手斩杀。虽被【巫主】以秘法复活，但实力已大不如前，可其经验与诡秘手段仍是天下顶尖。"
  },
  "晁玄": {
    "title": "橙圣使", "faction": "圣使组织", "former_faction": "太上灵虚道",
    "personality": "对力量与地位有病态的贪欲，心狠手辣，城府极深，自负傲慢，阴险诡谲。",
    "background": "原【太上灵虚道】掌门【轩辕野】的弟子。为盗取《太乙均天普化道元真经》法玄部和【三界逍遥旗】，设局陷害师父，是导致太上灵虚道衰落的罪魁祸首。",
    "appearance": "极少显露真容，常伪装成仙风道骨、面容清癯儒雅的中年道士，气质超然但暗藏诡秘压抑。",
    "techniques": ["《太乙均天普化道元真经》法玄部：包罗万象，诡异多变。"]
  },

  "通天真人": {
    "title": "黄圣使、玄天宗创始人", "faction": ["圣使组织", "玄天宗"], "realm": "化神期大圆满",
    "personality": "表面上悲天悯人，充满救世情怀；实则深谋远虑，将整个玄天宗都视为其完成“父亲”任务的工具与棋子。",
    "background": "【圣使组织】为夺取【八阵天玑图】而扶植的棋子，玄天宗的幕后主宰与创派祖师。",
    "public_identity": "玄天宗祖师"
  },
  "岳临风": {
    "title": "青圣使、云界仙宫宫主", "faction": ["圣使组织", "云界仙宫"], "realm": "化神后期",
    "personality": "天生的权谋家与野心家，心机深沉，视天下为棋盘，所有人皆为棋子。",
    "background": "“碧落之乱”的幕后导演之一，圣使组织的核心智囊，致力于从内部渗透瓦解正道。",
    "public_identity": "云界仙宫宫主"
  },
  "冥渊老祖": {
    "title": "绿圣使、血煞岛岛主", "faction": ["圣使组织", "血煞岛"],
